import './index.css';
import React, { useState } from 'react';
import { CloudIcon, FireIcon, MapPinIcon, SparklesIcon } from '@heroicons/react/24/outline';

function App() {
  const [city, setCity] = useState('Delhi');
  const [manualTemp, setManualTemp] = useState('');
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchCurrentTemp = async () => {
    if (!city.trim()) {
      setResult('Please enter a city name');
      return;
    }

    setLoading(true);
    setResult('Loading...');

    try {
      const response = await fetch(`/current-temp?city=${encodeURIComponent(city)}`);
      const data = await response.json();

      if (!response.ok) {
        setResult(`Error: ${data.error || JSON.stringify(data)}`);
        return;
      }

      setResult(`City: ${data.city} — Current: ${data.current_temp}°C — Predicted tomorrow: ${data.predicted_tomorrow}°C`);
    } catch (error) {
      setResult(`Network error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const predictFromManualTemp = async () => {
    const temp = parseFloat(manualTemp);
    if (isNaN(temp)) {
      setResult('Please enter a valid number');
      return;
    }

    setLoading(true);
    setResult('Predicting...');

    try {
      const response = await fetch('/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ today_temp: temp })
      });

      const data = await response.json();

      if (!response.ok) {
        setResult(`Error: ${data.error || JSON.stringify(data)}`);
        return;
      }

      setResult(`Today: ${data.today}°C — Predicted tomorrow: ${data.predicted_tomorrow}°C`);
    } catch (error) {
      setResult(`Network error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-400 via-purple-500 to-pink-500 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl">
        {/* Main Card */}
        <div className="bg-white/95 backdrop-blur-xl rounded-3xl shadow-2xl border border-white/20 overflow-hidden animate-fade-in">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 px-8 py-6 text-white">
            <div className="flex items-center justify-center gap-3 mb-2">
              <CloudIcon className="w-8 h-8" />
              <h1 className="text-3xl font-bold">Weather Predictor</h1>
              <SparklesIcon className="w-8 h-8" />
            </div>
            <p className="text-center text-blue-100 text-lg">
              Predict tomorrow's temperature with AI precision
            </p>
          </div>

          <div className="p-8 space-y-8">
            {/* City Temperature Section */}
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl p-6 border border-blue-100 hover:shadow-lg transition-all duration-300">
              <div className="flex items-center gap-3 mb-4">
                <MapPinIcon className="w-6 h-6 text-blue-600" />
                <h2 className="text-xl font-semibold text-gray-800">Get Current Temperature</h2>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    City Name
                  </label>
                  <input
                    type="text"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    placeholder="Enter city name..."
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all duration-200 text-lg"
                    onKeyPress={(e) => e.key === 'Enter' && fetchCurrentTemp()}
                  />
                </div>

                <button
                  onClick={fetchCurrentTemp}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
                >
                  <CloudIcon className="w-5 h-5" />
                  {loading ? 'Fetching...' : 'Use Current Temperature'}
                </button>
              </div>
            </div>

            {/* Divider */}
            <div className="flex items-center gap-4">
              <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
              <span className="text-gray-500 font-medium">OR</span>
              <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
            </div>

            {/* Manual Temperature Section */}
            <div className="bg-gradient-to-r from-purple-50 to-pink-50 rounded-2xl p-6 border border-purple-100 hover:shadow-lg transition-all duration-300">
              <div className="flex items-center gap-3 mb-4">
                <FireIcon className="w-6 h-6 text-purple-600" />
                <h2 className="text-xl font-semibold text-gray-800">Manual Temperature Input</h2>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Today's Temperature (°C)
                  </label>
                  <input
                    type="number"
                    step="0.1"
                    value={manualTemp}
                    onChange={(e) => setManualTemp(e.target.value)}
                    placeholder="e.g. 28.5"
                    className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all duration-200 text-lg"
                    onKeyPress={(e) => e.key === 'Enter' && predictFromManualTemp()}
                  />
                </div>

                <button
                  onClick={predictFromManualTemp}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 text-white font-semibold py-3 px-6 rounded-xl transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
                >
                  <SparklesIcon className="w-5 h-5" />
                  {loading ? 'Predicting...' : 'Predict Tomorrow\'s Temperature'}
                </button>
              </div>
            </div>

            {/* Results Section */}
            {result && (
              <div className={`rounded-2xl p-6 border-2 transition-all duration-500 ${
                result.includes('Error') || result.includes('Please') 
                  ? 'bg-red-50 border-red-200 text-red-800' 
                  : result.includes('Loading') || result.includes('Predicting')
                  ? 'bg-yellow-50 border-yellow-200 text-yellow-800'
                  : 'bg-green-50 border-green-200 text-green-800'
              }`}>
                <div className="flex items-center gap-3">
                  <div className={`w-3 h-3 rounded-full ${
                    result.includes('Error') || result.includes('Please')
                      ? 'bg-red-500'
                      : result.includes('Loading') || result.includes('Predicting')
                      ? 'bg-yellow-500 animate-pulse'
                      : 'bg-green-500'
                  }`}></div>
                  <p className="text-lg font-medium">{result}</p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Footer */}
        <div className="text-center mt-6 text-white/80">
          <p className="text-sm">Powered by advanced machine learning algorithms</p>
        </div>
      </div>
    </div>
  );
}

export default App;
